import React, { useCallback, useReducer } from 'react';
import './Department.css'
import Input from '../../shared/UIElements/Input';
import { VALIDATOR_REQUIRE,VALIDATOR_MINLENGTH } from '../../shared/UTIL/Validators';
import Button from '../../shared/UIElements/Button';

const formReducer =(state,action)=>{
    switch(action.type){
        case "INPUT_CHANGE":
            let formIsValid = true;
            for (const inputId in state.inputs){
                if(inputId === action.inputId){
                    formIsValid = formIsValid && action.isvalid
                }
                else{
                    formIsValid = formIsValid && state.inputs[inputId].isvalid
                }
            }
            return{
                ...state,

                inputs:{
                    ...state.inputs,
                    [action.inputId] : {value:action.value,isvalid:action.isvalid}
                },
                isvalid:formIsValid
            };
        default:
            return state;
    }
}

const NewEmployee = () => {

    const[formState,dispatch]=useReducer(formReducer,{
        inputs:{  // it stores the validity of each input
             title:{
                value: '',
                isvalid:false
            },
            description:{
                value:'',
                isvalid:false
            }
        },
        isvalid:false
    
    })

    const inputHandler =useCallback((id,value,isvalid)=>{
        dispatch({type:"INPUT_CHANGE",
            value:value, isvalid:isvalid,inputId:id});
    },[])


    const placeSumbitHandler = (event) =>{
        event.preventDefault();
        console.log(formState.inputs)
    }

    return(
        <form className='place-form' onSubmit={placeSumbitHandler}>
            {/* normal input tag for code reusability */}
            <Input id="title" element="input" type='text' label="TITLE" validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(4)]} // to make sure value is not empty 
            errorText="Please Enter Valid Title" onInput ={inputHandler}></Input>

            <Input id="description"  element="textarea" type='text' label="DESCRIPTION" validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(5)]} 
            errorText="Please Enter Valid Description With Atleast 5 Characters" onInput ={inputHandler} ></Input>

            <Button type="submit" disabled={!formState.isvalid}>ADD PLACE</Button>

        </form>
    )
}

export default NewPlace;
