import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import EmployeeList from "../components/EmployeeList";


const Department = () =>{
    const departmentId = useParams().departmentId; 

    const [loadedEmployees,setLoadedEmployees] = useState();
    const [error,setError] = useState();

    // useEffect to load the api
    useEffect(()=>{
        const fetchEmployees = async() =>{
            try{
                const response = await fetch(`http://localhost:5000/api/employee/department/${departmentId}`)
                const responseData = await response.json();
                console.log(responseData);
                if(!response.ok){
                    throw new Error(responseData.message);
                }
                setLoadedEmployees(responseData.employee);
            }
            catch(err){
                setError(err.message);
            }
        }
        fetchEmployees();
    },[departmentId]);
    return(
        <div>
            {loadedEmployees && <EmployeeList items={loadedEmployees}/>}
            {error && <div>{error}</div>}
        </div>
    )
}


export default Department;
